var button_default2 = ':host {\n  display: contents;\n  position: relative;\n}\n\n:where([part~=\'base\']) {\n  all: inherit;\n  display: inline-flex;\n}\n\n/*\n * Label\n */\n\n.prefix,\n.suffix {\n  flex: 0 0 auto;\n  display: flex;\n  align-items: center;\n  pointer-events: none;\n}\n\n.label {\n  display: inline-block;\n}\n\n.label::slotted(wa-icon) {\n  vertical-align: -2px;\n}\n\n/*\n * Caret modifier\n */\n\nwa-icon[part~=\'caret\'] {\n  display: flex;\n  align-self: center;\n  align-items: center;\n\n  &::part(svg) {\n    width: 0.875em;\n    height: 0.875em;\n  }\n\n  .wa-button:has(&) .suffix {\n    display: none;\n  }\n}\n\n/*\n * Loading modifier\n */\n\n.loading {\n  position: relative;\n  cursor: wait;\n\n  .prefix,\n  .label,\n  .suffix,\n  .caret {\n    visibility: hidden;\n  }\n\n  wa-spinner {\n    --indicator-color: currentColor;\n    --track-color: color-mix(in oklab, currentColor, transparent 90%);\n\n    position: absolute;\n    font-size: 1em;\n    height: 1em;\n    width: 1em;\n    top: calc(50% - 0.5em);\n    left: calc(50% - 0.5em);\n  }\n}\n\n/*\n * Badges\n */\n\nbutton ::slotted(wa-badge) {\n  border-color: var(--wa-color-surface-default);\n  position: absolute;\n  inset-block-start: 0;\n  inset-inline-end: 0;\n  translate: 50% -50%;\n  pointer-events: none;\n}\n\n:host(:dir(rtl)) ::slotted(wa-badge) {\n  translate: -50% -50%;\n}\n\n/*\n* Button spacing\n*/\n\nslot[name=\'prefix\']::slotted(*) {\n  margin-inline-end: var(--wa-space);\n}\n\nslot[name=\'suffix\']::slotted(*),\n.wa-button:not(.visually-hidden-label) [part~=\'caret\'] {\n  margin-inline-start: var(--wa-space);\n}\n'

